00190     private void setAsUsedLink(NetworkLink link) {
00191         LoggingHelper.LogFunction("Dialog Connection : Setting link as used link : "+link.toString());
00192         boolean marked = false;
00193         LoggingHelper.LogLogic("Dialog Connection : Looking for point-to-point and CSMA links!");
00194         for(NetworkLink l : dialogLink.links) {
00195             if (link.toString().equals(l.toString())) {
00196                 LoggingHelper.LogLogic("Dialog Connection : Match found!");
00197                 l.setUsed(true);
00198                 marked = true;
00199             }
00200         }
00201         if (!marked) {
00202             LoggingHelper.LogLogic("Dialog Connection : No match found! It is a wi-f- link.");
00203             for(NetworkLink l : dialogWiFiLink.links) {
00204                 if (link.toString().equals(l.toString())) {
00205                     l.setUsed(true);
00206                 }
00207             }
00208         }
00209     }
