00246             } else if (selectedTool == ToolStatus.TOOL_LINK_WIFI) {
00247                 type = LinkType.LINK_WIFI;
00248                 String list_nodes = this.nodes.stream().map(Object::toString).collect(Collectors.joining(", "));
00249                 this.lbl_deviceInfo.setText("Wi-Fi Connection between : "+list_nodes);
00250             }
00251             else { // assumed to be CSMA...
00252                 type = LinkType.LINK_CSMA;
00253                 String list_nodes = this.nodes.stream().map(Object::toString).collect(Collectors.joining(", "));
00254                 this.lbl_deviceInfo.setText("CSMA Connection between nodes : "+list_nodes);
00255             }
00256             this.comboBox_links.removeAllItems();
00257             this.comboBox_networks.removeAllItems();
00258             for (NetworkLink link : this.links) {
00259                 LoggingHelper.LogInfo("Type : "+link.getLinkType());
00260                 if (type == link.getLinkType()) {
00261                     LoggingHelper.LogInfo("Matched!");
00262                     this.comboBox_links.addItem(link);
00263                 }
00264             }
00265             for (Network n : this.networks) {
00266                 this.comboBox_networks.addItem(n);
00267             }
