00335     private static boolean openWebpage(URI uri) {
00336         Desktop desktop = Desktop.isDesktopSupported() ? Desktop.getDesktop() : null;
00337         if (desktop != null && desktop.isSupported(Desktop.Action.BROWSE)) {
00338             try {
00339                 desktop.browse(uri);
00340                 return true;
00341             } catch (Exception e) {
00342                 e.printStackTrace();
00343             }
00344         }
00345         return false;
00346     }
