00600     private void setUpEventListeners()
00601     {
00602         // ========================================= ALL EVENT LISTENERS ===============================================
00603         this.painter.addKeyListener(new KeyAdapter() {
00604             @Override
00605             public void keyPressed(KeyEvent e) {
00606                 super.keyPressed(e);
00607                 LoggingHelper.LogDebug("Key pressed : "+e.getKeyCode());
00608                 if (e.getKeyCode() == 32) {
00609                     switch (toolStatus) {
00610                         case TOOL_LINK_CSMA : {
00611                             // render the CSMA link...
00612                             painter.addAndPrintLink(new CsmaLinkPainter(nodesSelected,painter.getNodes()));
00613                             // instantiate connection dialog...
00614                             instantiateConnectionDialog();
00615                             // get to the device dialog and...
00616                             dialogConnection.showDialog(dialogLink.getAllLinks(),dialogNetwork.getAllNetworks(), nodesSelected,toolStatus);
00617                             // empty the nodes list...
00618                             nodesSelected = new ArrayList<>();
00619                             // empty the reference nodes list...
00620                             painter.setReferenceNodes(new ArrayList<>());
00621                         } break;
00622 
00623                         case TOOL_LINK_WIFI : {
00624                             // render the wi-fi link
00625                             painter.addAndPrintLink(new WifiLinkPainter(nodesSelected,painter.getNodes()));
00626                             // instantiate connection dialog
00627                             instantiateConnectionDialog();
00628                             // get the device dialog
00629                             dialogConnection.showDialog(dialogWiFiLink.getAllLinks(),dialogNetwork.getAllNetworks(), nodesSelected,toolStatus);
00630                             // empty the nodes list
00631                             nodesSelected = new ArrayList<>();
00632                             // empty the reference node list
00633                             painter.setReferenceNodes(new ArrayList<>());
00634                         }
00635                     }
00636                 }
00637             }
00638         });
00639 
00640         // record each mouse click on canvas...
00641         // do accordingly...
00642         this.painter.addMouseListener(new MouseAdapter() {
00643             @Override
00644             public void mouseClicked(MouseEvent e) {
00645                 super.mouseClicked(e);
00646                 LoggingHelper.LogDebug("Clicked at x : "+e.getX()+" y : "+e.getY());
00647                 switch (toolStatus) {
00648                     // if tool is 'node' tool then...
00649                     case TOOL_NODE : {
00650                         // create a new node at given point...
00651                         NodePainter node = new NodePainter(e.getX()-10, e.getY()-10, 20,String.valueOf(painter.getNodes().size()));
00652                         // add that node to painter for painting on canvas...
00653                         painter.addAndPrintNode(node);
00654                     } break;
00655 
00656                     // if tool is 'link' tool then...
00657                     case TOOL_LINK : {
00658                         int collision = -1;
00659                         boolean successfulClick = false;
00660                         // for first successful click....
00661                         // check if collision with any node...
00662                         collision = painter.pointCollideWithAny(e.getX(), e.getY());
00663                         LoggingHelper.LogDebug("Collision : "+collision+" Clicks : "+clicks);
00664                         if (collision >= 0 && clicks%2!=0) {
00665                             // if collision then change the information label and increment the no. of clicks...
00666                             firstNode = collision;
00667                             lbl_info.setText("Connection Tool Selected: First node selected now, select second node.");
00668                             successfulClick = true;
00669                         }
00670 
00671                         // for second successful click...
00672                         // check if collision with any node...
00673                         if (collision >= 0 && clicks%2==0) {
00674                             // if collision then change the information label, increment the no. of clicks and open a device configuration dialog box...
00675                             lbl_info.setText("Connection Tool Selected: To create a link, click on two nodes sequentially.");
00676                             painter.addAndPrintLink(new P2pLinkPainter(painter.getNodes().get(firstNode), painter.getNodes().get(collision)));
00677                             successfulClick = true;
00678                             LoggingHelper.LogDebug(firstNode+" "+collision);
00679                             instantiateConnectionDialog();
00680                             ArrayList<Integer> nodes = new ArrayList<>();
00681                             nodes.add(firstNode);
00682                             nodes.add(collision);
00683                             dialogConnection.showDialog(dialogLink.getAllLinks(),dialogNetwork.getAllNetworks(),nodes, toolStatus);
00684                             firstNode = -1;
00685                         }
00686                         incrementClicks(successfulClick);
00687                     } break;
00688 
00689                     case TOOL_LINK_CSMA, TOOL_LINK_WIFI: {
00690                        int collision = -1;
00691 
00692                        collision = painter.pointCollideWithAny(e.getX(), e.getY());
00693                        LoggingHelper.LogDebug("Collision : "+collision);
00694                        if (collision >= 0 && !nodesSelected.contains(collision)) {
00695                             nodesSelected.add(collision);
00696                             painter.addAndPrintRefNode(new NodePainter(painter.getNodes().get(collision).xPos-5, painter.getNodes().get(collision).yPos-5,30,"",Color.GREEN));
00697                        }
00698                     } break;
00699 
00700                 }
00701             }
00702         });
