00999     private void createFile() {
01000         if (this.lbl_server.getText().equalsIgnoreCase("Server Index :  Not configured")) {
01001             if (dialogHelper.showConfirmationDialog("There are no servers in the topology!\nDo you want to continue?","Warning!") == JOptionPane.NO_OPTION) {
01002                 return;
01003             }
01004         }
01005 
01006         if (this.lbl_client.getText().equalsIgnoreCase("Client Index  : Not configured")) {
01007             if (dialogHelper.showConfirmationDialog("There are no clients in the topology!","Warning!")==JOptionPane.NO_OPTION) {
01008                 return;
01009             }
01010         }
01011 
01012         String path = UNIVERSAL_SETTINGS.getString(OUTPUT_PATH)+"\\"+UNIVERSAL_SETTINGS.getString(FILE_NAME)+".txt";
01013 
01014         Map<String, String> otherFields = new HashMap<>();
01015         if (this.chkBox_netanim.isSelected()) {
01016             otherFields.put(CodeGenerator.UTILITY_NETANIM, CodeGenerator.VALUE_TRUE);
01017         } else {
01018             otherFields.put(CodeGenerator.UTILITY_NETANIM, CodeGenerator.VALUE_FALSE);
01019         }
01020 
01021         if (this.chkBox_wireshark.isSelected()) {
01022             otherFields.put(CodeGenerator.UTILITY_WIRESHARK,CodeGenerator.VALUE_TRUE);
01023         } else {
01024             otherFields.put(CodeGenerator.UTILITY_WIRESHARK,CodeGenerator.VALUE_FALSE);
01025         }
01026 
01027         otherFields.put(CodeGenerator.NAME_OF_TOPOLOGY, "Custom");
01028         otherFields.put(CodeGenerator.TOTAL_NODES, String.valueOf(this.painter.getNodes().size()));
01029         this.codeGenerator = new CodeGenerator(dialogConfigureServer,dialogConfigureClient,dialogConnection,dialogLink,dialogWiFiLink,otherFields);
01030         this.codeGenerator.GenerateCode();
01031         FileReaderWriter.writeUsingPath(this.codeGenerator.getCode(), path);
01032 
01033         this.dialogHelper.showInformationMsg("File has been generated successfully!\nAt : "+this.OutputPath, "Code Generated!");
01034     }
