01039     public static void main(String[] args) {
01040         try {
01041             // setting the UI theme, if it exists
01042             for(UIManager.LookAndFeelInfo info : UIManager.getInstalledLookAndFeels()) {
01043                 if ("Nimbus".equals(info.getName())) {
01044                     UIManager.setLookAndFeel(info.getClassName());
01045                 }
01046             }
01047         } catch (ClassNotFoundException e) {
01048             throw new RuntimeException(e);
01049         } catch (InstantiationException e) {
01050             throw new RuntimeException(e);
01051         } catch (IllegalAccessException e) {
01052             throw new RuntimeException(e);
01053         } catch (UnsupportedLookAndFeelException e) {
01054             throw new RuntimeException(e);
01055         }
01056         // enabling logger
01057         new LoggingHelper();
01058         // starting application
01059         new Home_Screen();
01060         // filtering the arguments...
01061         for (String a : args) {
01062             // enabling log level
01063             switch (a.trim().replace("-","").toUpperCase()) {
01064                 case "ERROR":
01065                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_ERROR);
01066                     break;
01067                 case "DEBUG":
01068                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_DEBUG);
01069                     break;
01070                 case "INFO":
01071                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_INFO);
01072                     break;
01073                 case "FUNCTION":
01074                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_FUNCTION);
01075                     break;
01076                 case "LOGIC":
01077                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_LOGIC);
01078                     break;
01079                 case "LOGALL":
01080                     LoggingHelper.setLogLevel(LogLevel.LOG_LEVEL_ALL);
01081                 default:
01082                     LoggingHelper.setLogLevel(LogLevel.LOG_NONE);
01083                     break;
01084             }
01085         }
01086     }
